pragma solidity ^0.8.28;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

// SPDX-License-Identifier: MIT

contract Counter {
    // State variable to store the counter value, initialized to 0
    int256 public count = 0;

    // Event emitted when the counter changes
    event CounterChanged(int256 newCount, address triggeredBy);

    // Function to increment the counter
    function increment() public {
        count += 1;
        emit CounterChanged(count, msg.sender);
    }

    // Function to decrement the counter
    function decrement() public {
        count -= 1;
        emit CounterChanged(count, msg.sender);
    }

    // Function to reset the counter to zero
    function reset() public {
        count = 0;
        emit CounterChanged(count, msg.sender);
    }

    // Function to set the counter to a specific value
    function setCount(int256 newCount) public {
        count = newCount;
        emit CounterChanged(count, msg.sender);
    }
}

